import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { usePerformanceAPI } from './hooks/usePerformanceAPI';
import TestConfiguration from './components/TestConfiguration';
import RealTimeMonitoring from './components/RealTimeMonitoring';
import TestResults from './components/TestResults';
import AIAnalysis from './components/AIAnalysis';
import TestHistory from './components/TestHistory';
import { Activity, Brain, Zap, BarChart3, History, Settings } from 'lucide-react';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [currentTest, setCurrentTest] = useState(null);
  const [testResults, setTestResults] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  
  const { connectionStatus, loading, error, backendUrl } = usePerformanceAPI();

  const tabs = [
    { id: 'dashboard', label: 'Dashboard', icon: Activity },
    { id: 'config', label: 'Test Config', icon: Settings },
    { id: 'monitoring', label: 'Monitoring', icon: BarChart3 },
    { id: 'results', label: 'Results', icon: Zap },
    { id: 'analysis', label: 'AI Analysis', icon: Brain },
    { id: 'history', label: 'History', icon: History },
  ];

  const handleStartTest = async (config) => {
    try {
      const result = await startTest(config);
      setCurrentTest(result);
      setActiveTab('monitoring');
    } catch (error) {
      console.error('Failed to start test:', error);
    }
  };

  const handleTestComplete = (results) => {
    setTestResults(results);
    setCurrentTest(null);
    setActiveTab('results');
  };

  const handleAnalyze = async (data) => {
    try {
      const analysisResult = await analyzeResults(data);
      setAnalysis(analysisResult);
      setActiveTab('analysis');
    } catch (error) {
      console.error('Failed to analyze results:', error);
    }
  };

  const getConnectionStatusColor = () => {
    switch (connectionStatus) {
      case 'connected': return 'text-green-500';
      case 'disconnected': return 'text-red-500';
      case 'checking': return 'text-yellow-500';
      default: return 'text-gray-500';
    }
  };

  const getConnectionStatusText = () => {
    switch (connectionStatus) {
      case 'connected': return 'Connected';
      case 'disconnected': return 'Disconnected';
      case 'checking': return 'Checking...';
      default: return 'Unknown';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <header className="bg-white/10 backdrop-blur-md border-b border-white/20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Brain className="h-8 w-8 text-purple-400" />
                <h1 className="text-xl font-bold text-white">Ludo Performance Suite</h1>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${connectionStatus === 'connected' ? 'bg-green-400' : connectionStatus === 'checking' ? 'bg-yellow-400' : 'bg-red-400'}`}></div>
                <span className={`text-sm ${getConnectionStatusColor()}`}>
                  {getConnectionStatusText()}
                </span>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="text-xs text-gray-400">
                Backend: {backendUrl}
              </div>
              {error && (
                <div className="text-xs text-red-400 bg-red-900/20 px-2 py-1 rounded">
                  {error}
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="bg-white/5 backdrop-blur-sm border-b border-white/10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === tab.id
                      ? 'border-purple-400 text-purple-400'
                      : 'border-transparent text-gray-300 hover:text-white hover:border-gray-300'
                  }`}
                >
                  <Icon className="h-4 w-4" />
                  <span>{tab.label}</span>
                </button>
              );
            })}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          {activeTab === 'dashboard' && (
            <div className="space-y-6">
              <div className="text-center">
                <h2 className="text-3xl font-bold text-white mb-4">
                  AI-Powered Performance Testing
                </h2>
                <p className="text-gray-300 max-w-2xl mx-auto">
                  Configure and execute comprehensive performance tests with real-time monitoring 
                  and intelligent AI analysis. Get instant insights and recommendations.
                </p>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <div className="card">
                  <div className="flex items-center space-x-3">
                    <Settings className="h-8 w-8 text-blue-400" />
                    <div>
                      <h3 className="text-lg font-semibold text-white">Test Configuration</h3>
                      <p className="text-gray-400">Set up your performance tests</p>
                    </div>
                  </div>
                </div>
                
                <div className="card">
                  <div className="flex items-center space-x-3">
                    <BarChart3 className="h-8 w-8 text-green-400" />
                    <div>
                      <h3 className="text-lg font-semibold text-white">Real-time Monitoring</h3>
                      <p className="text-gray-400">Watch tests in progress</p>
                    </div>
                  </div>
                </div>
                
                <div className="card">
                  <div className="flex items-center space-x-3">
                    <Brain className="h-8 w-8 text-purple-400" />
                    <div>
                      <h3 className="text-lg font-semibold text-white">AI Analysis</h3>
                      <p className="text-gray-400">Get intelligent insights</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'config' && (
            <TestConfiguration 
              onStartTest={handleStartTest}
              loading={loading}
            />
          )}

          {activeTab === 'monitoring' && (
            <RealTimeMonitoring 
              currentTest={currentTest}
              onTestComplete={handleTestComplete}
            />
          )}

          {activeTab === 'results' && (
            <TestResults 
              results={testResults}
              onAnalyze={handleAnalyze}
            />
          )}

          {activeTab === 'analysis' && (
            <AIAnalysis 
              analysis={analysis}
              loading={loading}
            />
          )}

          {activeTab === 'history' && (
            <TestHistory />
          )}
        </motion.div>
      </main>
    </div>
  );
}

export default App;
